import{gsap}from"gsap";import{ScrollTrigger}from"gsap/ScrollTrigger";gsap.registerPlugin(ScrollTrigger);export function slideInRight(r,e){(new gsap.timeline).from(e,{duration:3,x:-500,alpha:0,scrollTrigger:{trigger:r,start:"top center%",end:"bottom  80%",toggleActions:"restart none resume pause",markers:!0,scrub:!0}})}export function slideInLeft(r,e){(new gsap.timeline).from(e,{duration:1,x:500,alpha:0,scrollTrigger:{trigger:r,toggleActions:"restart none resume none",markers:!0,scrub:!0}})}